import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import '../../App.css';
import './SignUp.css'
import Validation from './SignupValidation.js';

import user_icon from '../Assests/person.png';
import email_icon from '../Assests/email.png';
import password_icon from '../Assests/password.png';

export default function SignUp() {
    const [values, setValues] = useState({  
        name: '',    
        email:'',
        password:''
    })

    const [errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }
    
    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
    }

    return (
        <div className='login-container'>
        <div className='login-form'>
          <h1>Sign Up</h1>
          <form action='' onSubmit={handleSubmit}>
            <div className='form-group'>
              <label htmlFor='name'>Name</label>
              <input type='name' placeholder='Enter Name'
              name='name' onChange={handleInput} className='form-control'/>
              <span>{errors.email && <span className='text-danger'>{errors.email}</span>}</span>
            </div>
            <div className='form-group'>
              <label htmlFor='email'>Email</label>
              <input type='email' placeholder='Enter Email'
              name='email' onChange={handleInput}  className='form-control' />
              <span>{errors.email && <span className='text-danger'>{errors.email}</span>}</span>
            </div>
            <div className='form-group'>
              <label htmlFor='password'>Password</label>
              <input type='password' placeholder='Enter Password'
              name='password' onChange={handleInput}  className='form-control' />
              <span>{errors.password && <span className='text-danger'>{errors.password}</span>}</span>
            </div>
            <button className='btn btn-success w-100'>Sign Up</button>
            <p>You agree to our terms and policies</p>
            <Link to='/login' className='create-account-link'>Login</Link>
  
          </form>
        </div>
      </div>
    )
}
